mapper.select:

	select 
		REVIEWNO, CARTNO, PRODUCTCODE, PAYCODE,
		ID, NAME, CONTENT, REVIEWPOINT, REVIEWDATE
	from (
		select /*+ INDEX_DESC(TBL_REVIEW PK_REVIEW) */
		rownum rn, REVIEWNO, CARTNO, PRODUCTCODE, PAYCODE,
				   ID, NAME, CONTENT, REVIEWPOINT, REVIEWDATE
		from TBL_REVIEW
		where productCode = #{productCode} and rownum <= #{criteria.pageNum} * #{criteria.amount}
		)
	where rn > (#{criteria.pageNum} -1) * #{criteria.amount}

select *
from TBL_PAYMENT
where id = #{id} and productCode = #{productCode}
